[1]: Junit

 public void test437522() throws Exception {
    	String projectName = new String("test437522");
    	try {
            createJavaProject(projectName, new String[] {"src"}, new String[] {"JCL_LIB"}, "bin", "1.8");
    		createFolder("/" + projectName + "/src/a");
    		String contents = "package a;\n" +
				"public interface T {\n" +
				"    int f = 0;\n" +
				"}\n";
    		createFile("/" + projectName + "/src/a/T.java", contents);
    		createFolder("/" + projectName + "/src/b");
    		contents = "package b;\n" +
				"public class X  implements a.T {\n" +
				"	int i = a.T.super.f;\n" +
				"}\n";
    		createFile("/" + projectName + "/src/b/X.java", contents);
    		ICompilationUnit cuT = getCompilationUnit("/" + projectName + "/src/a/T.java");
    		ICompilationUnit cuX = getCompilationUnit("/" + projectName + "/src/b/X.java");

    		ASTParser parser = ASTParser.newParser(AST.JLS8);
    		parser.setSource(cuT);
    		parser.setResolveBindings(true);
    		parser.setStatementsRecovery(true);
    		CompilationUnit astRoot = (CompilationUnit) parser.createAST(null);

    		parser = ASTParser.newParser(AST.JLS8);
    		parser.setSource(cuX);
    		parser.setResolveBindings(true);
    		parser.setStatementsRecovery(true);
    		astRoot = (CompilationUnit) parser.createAST(null);
    		ImportRewrite rewrite = ImportRewrite.create(astRoot, false);
    		apply(rewrite);
    		contents = "package b;\n" +
    			"import static a.T.f;\n" +
				"public class X {\n" +
				"	int i = a.T.super.f;\n" +
				"}\n";
    		 cuX = getCompilationUnit("/" + projectName + "/src/b/X.java");
    		 assertEqualStringIgnoreDelim(cuX.getSource(), contents);
    	} finally {
    		deleteProject(projectName);
    	}
	}
