import org.eclipse.jdt.annotation.NonNull;

public class Tools2 {
	@SafeVarargs
	public static <T> List<@NonNull T> asList(T... ts) {
		@SuppressWarnings("null")
		@NonNull
		List<@NonNull T> res = Arrays.asList(ts);
		return res;
	}

