  private static <A1> void foo(Consumer<A1> c) { }
  private static void baz(String s1, String s2) { }
  private static void bar(VoidMethodRef2<String, String> mr2) { }
  private static interface VoidMethodRef2<A1, A2> extends BiConsumer<A1, A2>, Serializable { }
}
